  cursor: pointer;
}
.main-logo-border {
  position: absolute;
  transform: scale(1.05) translateY(-1%);
  path { fill: $white; }
}
.main-logo {
  position: relative;
  clip-path: url(#main-logo);
  overflow: hidden;
  &__ratio {
    height: 0;
    padding-top: 69.338%;
  }
  &__bg {
    position: absolute;
    width: 150%;
    padding-top: 150%;
    top: -50%;
    left: -25%;
    background: radial-gradient(farthest-corner at 85% 85%, $secondary, $purple);
    animation: 10s rotation infinite linear;
    pointer-events: none;
  }
  svg { position: absolute; }
}

@keyframes rotation {
  0%   { transform: rotate(0deg)   rotateZ(360deg); }
  100% { transform: rotate(360deg) rotateZ(360deg); }
}

</style>
